script:
frappe.ui.form.on('Customer', {
    after_save: function(frm) {
        hide_button(frm);
    },

    onload: function(frm) {
        hide_button(frm);
    }
});

function hide_button(frm) {
    setTimeout(() => {
        frm.remove_custom_button('Accounting Ledger', 'View');
        frm.remove_custom_button('Accounts Receivable', 'View');
        frm.remove_custom_button('Pricing Rule', 'Create');
        frm.remove_custom_button('Get Customer Group Details', 'Actions');
    }, 10); // Ajuste le délai si nécessaire
}

frappe.ui.form.on('Customer', {
    refresh(frm) {
        // Ajouter le bouton "Récupérer GPS Principal" sous le menu "GPS"
        frm.add_custom_button(__('Récupérer GPS Principal'), function() {
            // Vérifier si la géolocalisation est prise en charge
            if ("geolocation" in navigator) {
                navigator.geolocation.getCurrentPosition(function(position) {
                    // Récupérer les coordonnées GPS
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;
                    const gpsCoords = `${lat}, ${lon}`;

                    // Mettre à jour le champ 'custom_gps' avec les coordonnées GPS récupérées
                    frm.set_value('custom_gps', gpsCoords);

                    // Enregistrer automatiquement le document après la mise à jour du champ
                    frm.save();

                    // Optionnel : Afficher un message de succès
                    // frappe.msgprint(`Coordonnées GPS récupérées et enregistrées : ${gpsCoords}`);
                }, function(error) {
                    // Gérer les erreurs
                    frappe.msgprint(`Erreur lors de la récupération des coordonnées GPS : ${error.message}`);
                }, {
                    // Options de géolocalisation
                    enableHighAccuracy: true,
                    timeout: 5000,
                    maximumAge: 0
                });
            } else {
                frappe.msgprint("La géolocalisation n'est pas prise en charge par ce navigateur.");
            }
        }, __("Localiser"));

        // Ajouter le bouton "Récupérer GPS Livraison" sous le menu "GPS"
        frm.add_custom_button(__('Récupérer GPS Livraison'), function() {
            // Vérifier si la géolocalisation est prise en charge
            if ("geolocation" in navigator) {
                navigator.geolocation.getCurrentPosition(function(position) {
                    // Récupérer les coordonnées GPS
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;
                    const gpsCoords = `${lat}, ${lon}`;

                    // Mettre à jour le champ 'custom_gps_livraison' avec les coordonnées GPS récupérées
                    frm.set_value('custom_gps_livraison', gpsCoords);

                    // Enregistrer automatiquement le document après la mise à jour du champ
                    frm.save();

                    // Optionnel : Afficher un message de succès
                    // frappe.msgprint(`Coordonnées GPS Livraison récupérées et enregistrées : ${gpsCoords}`);
                }, function(error) {
                    // Gérer les erreurs
                    frappe.msgprint(`Erreur lors de la récupération des coordonnées GPS : ${error.message}`);
                }, {
                    // Options de géolocalisation
                    enableHighAccuracy: true,
                    timeout: 5000,
                    maximumAge: 0
                });
            } else {
                frappe.msgprint("La géolocalisation n'est pas prise en charge par ce navigateur.");
            }
        }, __("Localiser"));
    }
});

frappe.ui.form.on('Customer', {
    refresh(frm) {
        if (frm.doc.custom_gps) {
            const [latitude, longitude] = frm.doc.custom_gps.split(", ");
            const mapUrl = `https://www.google.com/maps/embed/v1/place?key=AIzaSyBhtddbDMEu4OoBJAxlfYADptjTspOqflw&q=${latitude},${longitude}&zoom=15`;

            $(frm.fields_dict['custom_carte'].wrapper).html(`<iframe width="100%" height="200" frameborder="0" style="border:1" src="${mapUrl}" allowfullscreen></iframe>`);
        }
    }
});

frappe.ui.form.on('Visite Commerciale', {
    refresh: function(frm) {
        // Vérifier si l'état du workflow est "En Cours" et que les champs ne sont pas encore mis à jour
        if (frm.doc.status === "En Cours" && !frm.doc.gps_visite && !frm.doc.heure_debut_visite && !frm.doc.carte) {
            // Mettre à jour le champ 'gps_visite' avec les coordonnées GPS actuelles
            if ("geolocation" in navigator) {
                navigator.geolocation.getCurrentPosition(function(position) {
                    var latitude = position.coords.latitude;
                    var longitude = position.coords.longitude;
                    var gpsCoords = latitude + ', ' + longitude;
                    var now = frappe.datetime.now_datetime();  // Récupérer la date et l'heure actuelles

                    // Mettre à jour les champs
                    frm.set_value('gps_visite', gpsCoords);
                    frm.set_value('heure_debut_visite', now);  // Met à jour l'heure de la visite avec l'heure actuelle
                    frm.set_value('carte', `https://www.google.com/maps?q=${latitude},${longitude}`);  // Génère un lien Google Maps
                    frm.set_value('status', 'En Cours');  // Met à jour le champ 'status' pour refléter l'état actuel du workflow

                    // Enregistrer automatiquement le document après mise à jour des champs
                    frm.save();
                }, function(error) {
                    frappe.msgprint(__('Erreur lors de la récupération de la localisation : {0}', [error.message]));
                }, {
                    enableHighAccuracy: true,
                    timeout: 5000,
                    maximumAge: 0
                });
            } else {
                frappe.msgprint(__('La géolocalisation n\'est pas prise en charge par votre navigateur.'));
            }
        }

        // Afficher la carte si les coordonnées GPS sont déjà disponibles
        if (frm.doc.gps_visite) {
            const [latitude, longitude] = frm.doc.gps_visite.split(", ");
            const mapUrl = `https://www.google.com/maps/embed/v1/place?key=AIzaSyBhtddbDMEu4OoBJAxlfYADptjTspOqflw&q=${latitude},${longitude}&zoom=15`;

            $(frm.fields_dict['carte'].wrapper).html(`<iframe width="100%" height="300" frameborder="0" style="border:0" src="${mapUrl}" allowfullscreen></iframe>`);
        }
    }
});

frappe.ui.form.on('Visite Commerciale', {
    refresh: function(frm) {
        // Vérifier si l'état du workflow est "Terminé" et que le champ 'heure_fin_visite' n'est pas encore mis à jour
        if (frm.doc.status === "Terminé" && !frm.doc.heure_fin_visite) {
            var now = frappe.datetime.now_datetime();  // Récupérer la date et l'heure actuelles

            // Mettre à jour le champ 'heure_fin_visite' avec l'heure actuelle
            frm.set_value('heure_fin_visite', now).then(function() {
                // Calculer la durée entre 'heure_debut_visite' et 'heure_fin_visite'
                if (frm.doc.heure_debut_visite && frm.doc.heure_fin_visite) {
                    var start_time = moment(frm.doc.heure_debut_visite);
                    var end_time = moment(frm.doc.heure_fin_visite);
                    var duration = moment.duration(end_time.diff(start_time));
                    var duration_in_minutes = Math.round(duration.asMinutes());  // Arrondir la durée à l'entier le plus proche

                    // Mettre à jour le champ 'duree_visite' avec la durée calculée en minutes sans décimales
                    frm.set_value('duree_visite', duration_in_minutes);
                }

                // Enregistrer automatiquement le document après mise à jour des champs
                frm.save();
            });
        }
    }
});

frappe.ui.form.on('Opportunite', {
    refresh: function(frm) {
        // Bouton "Appeler" sous le groupe "Créer"
        frm.add_custom_button(__('Appel Téléphonique'), function() {
            let phone_number = frm.doc.telephone_client;
            if (phone_number) {
                // Créer un nouvel enregistrement 'Appel Téléphonique' avec le nom complet de l'utilisateur actuel
                frappe.db.insert({
                    doctype: 'Appel Telephonique',
                    client: frm.doc.client,
                    date: frappe.datetime.get_today(),
                    heure: frappe.datetime.now_datetime(),
                    utilisateur: frappe.user.full_name(),
                    opportunite: frm.doc.name
                }).then(function(doc) {
                    frappe.msgprint(__('Un nouvel appel téléphonique a été enregistré.'));
                    // Naviguer vers le document nouvellement créé
                    frappe.set_route('Form', 'Appel Telephonique', doc.name);
                });

                // Ouvrir l'interface téléphonique avec le numéro
                window.location.href = 'tel:' + phone_number;
            } else {
                frappe.msgprint(__('Aucun numéro de téléphone mobile n\'est défini pour ce client.'));
            }
        }, __("Créer")); // Le bouton est ajouté au groupe "Créer"

        // Bouton "Créer Visite" sous le groupe "Créer"
        frm.add_custom_button(__('Visite Commerciale'), function() {
            // Créer un nouvel enregistrement 'Visite Commerciale' avec le nom complet de l'utilisateur actuel et la date
            frappe.db.insert({
                doctype: 'Visite Commerciale',
                client: frm.doc.client,
                date: frappe.datetime.get_today(),
                utilisateur: frappe.user.full_name(),
                opportunite: frm.doc.name
            }).then(function(doc) {
                frappe.msgprint(__('Une nouvelle visite commerciale a été créée.'));
                // Naviguer vers le document nouvellement créé
                frappe.set_route('Form', 'Visite Commerciale', doc.name);
            });
        }, __("Créer")); // Le bouton est ajouté au groupe "Créer"
    }
});

