frappe.listview_settings['Customer'].get_indicator = function(doc) {
    if (doc.custom_status === 'Prospect') {
        return [__('Prospect'), 'blue', 'custom_status,=,Prospect'];
    } else if (doc.custom_status === 'Actif') {
        return [__('Actif'), 'green', 'custom_status,=,Actif'];
    } else if (doc.custom_status === 'Dormant') {
        return [__('Dormant'), 'orange', 'custom_status,=,Dormant'];
    } else if (doc.custom_status === 'Perdu') {
        return [__('Perdu'), 'red', 'custom_status,=,Perdu'];
    } else if (doc.custom_status === 'Exclu') {
        return [__('Exclu'), 'gray', 'custom_status,=,Exclu'];
    }
};

frappe.ui.form.on('Customer', {
    custom_qualité_fréquence: function(frm) {
        valider_champ(frm, 'custom_qualité_fréquence');
        calculer_moyenne(frm);
    },
    custom_qualité_interaction: function(frm) {
        valider_champ(frm, 'custom_qualité_interaction');
        calculer_moyenne(frm);
    },
    custom_qualité_paiements: function(frm) {
        valider_champ(frm, 'custom_qualité_paiements');
        calculer_moyenne(frm);
    }
});

function valider_champ(frm, fieldname) {
    let valeur = frm.doc[fieldname];
    if (valeur < 0 || valeur > 10) {
        frappe.msgprint(__('La valeur de {0} doit être comprise entre 0 et 10.', [fieldname]));
        frm.set_value(fieldname, '');
    }
}

function calculer_moyenne(frm) {
    let freq = frm.doc.custom_qualité_fréquence || 0;
    let interaction = frm.doc.custom_qualité_interaction || 0;
    let paiements = frm.doc.custom_qualité_paiements || 0;

    let moyenne = (freq + interaction + paiements) / 3;

    // Convertir la moyenne en une valeur de type évaluation (0 à 1)
    let evaluation = moyenne / 10;

    // Mettre à jour le champ custom_qualité_client
    frm.set_value('custom_qualité_client', evaluation);
}

frappe.ui.form.on('Quotation', {
