script:
frappe.call({
    method: "frappe.client.get_list",
    args: {
        doctype: "Customer",
        fields: ["name", "customer_name", "custom_status", "custom_wilaya", "modified"],
        order_by: "modified desc",
        limit: 5
    },
    callback: function(r) {
        // Utilisez root_element pour cibler l'√©l√©ment
        var customerListElement = root_element.querySelector('#customer-list');
        if (!customerListElement) {
            console.error('√âl√©ment #customer-list introuvable dans root_element.');
            return;
        }
        if (r.message && r.message.length > 0) {
            var customers = r.message.slice(0, 5);
            var html = '<div id="customers-table-container">';
            html += '<table id="customers-table">';
            html += '<thead><tr>';
            html += '<th>Nom du Client</th>';
            html += '<th>Statut</th>';
            html += '<th>Wilaya</th>';
            html += '<th>Derni√®re Interaction</th>';
            html += '</tr></thead><tbody>';
            customers.forEach(function(customer) {
                // D√©termine la couleur bas√©e sur le statut
                var statusColor = '';
                switch (customer.custom_status) {
                    case 'Prospect':
                        statusColor = 'color: blue;';
                        break;
                    case 'Actif':
                        statusColor = 'color: green;';
                        break;
                    case 'Dormant':
                        statusColor = 'color: orange;';
                        break;
                    case 'Perdu':
                        statusColor = 'color: red;';
                        break;
                    case 'Exclu':
                        statusColor = 'color: gray;';
                        break;
                    default:
                        statusColor = 'color: black;';
                }

                // Formate la date pour une meilleure lisibilit√©
                var formattedDate = formatDate(customer.modified);

                // Ajoute une ligne cliquable avec l'URL du client
                html += `
                    <tr class="clickable-row" onclick="window.location.href='/app/customer/${customer.name}'">
                        <td>${customer.customer_name || '-'}</td>
                        <td style="${statusColor}">${customer.custom_status || '-'}</td>
                        <td>${customer.custom_wilaya || '-'}</td>
                        <td>${formattedDate || '-'}</td>
                    </tr>
                `;
            });
            html += '</tbody></table></div>';
            customerListElement.innerHTML = html;
        } else {
            customerListElement.innerHTML = 'Aucun client trouv√©.';
        }
    },
    error: function(err) {
        var customerListElement = root_element.querySelector('#customer-list');
        if (customerListElement) {
            customerListElement.innerHTML = 'Erreur lors du chargement des donn√©es.';
        }
        console.error('Erreur lors du chargement des donn√©es :', err);
    }
});

// Fonction pour reformater une date au format DD/MM/YYYY HH:MM
function formatDate(dateString) {
    if (!dateString) return '';
    var date = new Date(dateString);
    var day = String(date.getDate()).padStart(2, '0');
    var month = String(date.getMonth() + 1).padStart(2, '0'); // Les mois commencent √† 0
    var year = date.getFullYear();
    var hours = String(date.getHours()).padStart(2, '0');
    var minutes = String(date.getMinutes()).padStart(2, '0');
    return `${day}/${month}/${year} ${hours}:${minutes}`;
}

style:
#customers-table-container {
    overflow-x: auto; /* Scroll horizontal si n√©cessaire */
    margin-bottom: 15px; /* Espace pour un affichage propre */
}

#customers-table {
    border-collapse: separate; /* Permet d'appliquer des bordures arrondies */
    border-spacing: 0; /* Pas d'espace entre les cellules */
    width: 100%;
    border: 0.5px solid rgba(211, 211, 211, 0.6); /* Bordure fine en couleur claire */
    border-radius: 8px; /* Coins arrondis */
    overflow: hidden; /* S'assure que les coins arrondis s'appliquent correctement */
    font-size: 12px;
}

#customers-table th,
#customers-table td {
    border: 0.5px solid rgba(211, 211, 211, 0.6); /* Bordure fine autour des cellules */
    padding: 8px;
    text-align: center;
}

#customers-table th {
    background-color: #f9f9f9; /* Fond clair pour l'en-t√™te */
    font-weight: bold; /* Texte en gras */
}

#customers-table tbody tr:hover {
    background-color: #f2f2f2; /* Fond l√©ger au survol */
}

/* Coins arrondis sp√©cifiques */
#customers-table thead tr:first-child th:first-child {
    border-top-left-radius: 8px; /* Coin en haut √† gauche */
}

#customers-table thead tr:first-child th:last-child {
    border-top-right-radius: 8px; /* Coin en haut √† droite */
}

#customers-table tbody tr:last-child td:first-child {
    border-bottom-left-radius: 8px; /* Coin en bas √† gauche */
}

#customers-table tbody tr:last-child td:last-child {
    border-bottom-right-radius: 8px; /* Coin en bas √† droite */
}

/* Style pour les lignes cliquables */
.clickable-row {
    cursor: pointer; /* Change le curseur pour indiquer que la ligne est cliquable */
    transition: background-color 0.3s; /* Ajoute une transition pour l'effet de survol */
}

.clickable-row:hover {
    background-color: #f2f2f2; /* Change le fond au survol */
}

script:
// R√©cup√©rer la date du jour
var dateDuJour = new Date();

// Obtenir les composants de la date
var jour = dateDuJour.getDate();
var mois = dateDuJour.getMonth() + 1; // Les mois commencent √† 0, donc ajout de 1
var annee = dateDuJour.getFullYear();
var title = frappe.session.user_fullname;

// Formater la date
var dateFormatee = jour + '/' + mois + '/' + annee;

// R√©cup√©rer l'√©l√©ment avec la classe "some-class" dans le framework Frappe
var someClassElement = root_element.querySelector('.some-class');
var nom = root_element.querySelector('.nom');

// Mettre √† jour le contenu de l'√©l√©ment avec la date format√©e

nom.innerHTML = "Bonjour <strong>" + title + "</strong>";
someClassElement.innerHTML = dateFormatee;

frappe.call({
    method: "frappe.client.get",
    args: {
        doctype: "User",
        name: frappe.session.user, // R√©cup√©rer l'utilisateur connect√©
    },
    callback(r) {
        if (r.message) {
            const userImageContainer = root_element.querySelector('.user-image-container');
            const userImageElement = root_element.querySelector('#imageUser');

            // Supprimer les enfants existants (au cas o√π)
            userImageContainer.innerHTML = "";

            // R√©cup√©rer l'image de l'utilisateur
            var userImageURL = r.message.user_image;

            // V√©rifier si l'image existe
            if (userImageURL) {
                // Afficher l'image utilisateur
                userImageElement.src = userImageURL;
                userImageElement.style.display = "block";
                userImageContainer.appendChild(userImageElement);
            } else {
                // G√©n√©rer un avatar avec les initiales
                const fullName = r.message.full_name || frappe.session.user_fullname || "Utilisateur";
                const initials = fullName.split(" ").map(word => word[0]).join("").slice(0, 2);

                // Cr√©er l'√©l√©ment avatar
                const avatarElement = document.createElement("div");
                avatarElement.className = "user-avatar";
                avatarElement.innerText = initials;

                userImageContainer.appendChild(avatarElement);
            }
        }
    }
});

style:
/* Conteneur principal */
.header-container {
  display: flex;
  flex-wrap: wrap; /* Permet aux √©l√©ments de passer √† la ligne si n√©cessaire */
  align-items: center; /* Aligne les √©l√©ments verticalement au centre */
  background-color: #ffffff; /* Fond blanc */
  padding: 1rem; /* Espacement interne */
  border-radius: 0.75rem; /* Coins arrondis */
  border: 0.5px solid #e5e7eb; /* Bordure grise tr√®s fine */
  width: 100%;
  gap: 1rem; /* Espacement entre les sections */
  box-sizing: border-box; /* Inclut le padding dans la largeur totale */
}

/* Conteneur de l'image utilisateur */
.user-image-container {
  flex: 0 0 auto; /* Ne s'√©tire pas */
  width: 3rem; /* Taille ajust√©e */
  height: 3rem;
  border-radius: 50%;
  overflow: hidden;
  border: 0.5px solid #e5e7eb;
  display: flex;
  justify-content: center;
  align-items: center;
}

/* Image utilisateur */
.user-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Avatar utilisateur */
.user-avatar {
  width: 3rem;
  height: 3rem;
  border-radius: 50%;
  background-color: #457b9d;
  color: #ffffff;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 1rem;
  font-weight: 600;
  text-transform: uppercase;
}

/* Contenu textuel */
.text-content {
  flex: 1; /* Prend tout l'espace restant */
  display: flex;
  flex-direction: column; /* Affiche le texte en colonne */
  justify-content: center;
  text-align: left; /* Aligne le texte √† gauche */
  gap: 0.2rem; /* R√©duction de l'espacement entre les lignes */
}

.title {
  font-size: 1rem; /* Taille r√©duite */
  font-weight: 600; /* Gras */
  color: #1f2937; /* Couleur sombre */
  margin: 0;
}

.subtitle {
  font-size: 0.8rem;
  font-weight: 400;
  color: #6b7280;
  margin: 0;
}

/* Contenu de la date */
.date-content {
  flex: 0 0 auto; /* Ne s'√©tire pas */
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-left: auto;
}

.date {
  font-size: 0.8rem;
  font-weight: 400;
  color: #1f2937;
  margin: 0;
  white-space: nowrap;
}

/* Ic√¥ne */
.icon {
  width: 1.5rem;
  height: 1.5rem;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f3f4f6;
  border-radius: 50%;
  color: #1f2937;
}

/* M√©dia queries */
@media (max-width: 768px) {
  .header-container {
    flex-wrap: nowrap; /* Emp√™che les √©l√©ments de passer √† la ligne */
    text-align: left; /* Texte align√© √† gauche */
    gap: 1rem; /* Espacement suffisant entre les sections */
  }

  .text-content {
    text-align: left; /* Aligne toujours le texte √† gauche */
  }

  .date-content {
    margin-left: auto; /* Place la date √† l'extr√©mit√© droite */
  }
}

@media (max-width: 480px) {
  .header-container {
    flex-direction: column; /* Sur tr√®s petits √©crans, tout passe en colonne */
    align-items: flex-start; /* Aligne les √©l√©ments √† gauche */
  }

  .text-content {
    text-align: left; /* Assure que le texte reste align√© √† gauche */
    gap: 0.5rem; /* Espacement r√©duit entre le titre et le sous-titre */
  }

  .date-content {
    margin-left: 0; /* Supprime l'espacement gauche */
    justify-content: flex-start; /* Aligne la date √† gauche */
  }
}
