/* 
  Container principal :
  - 4 colonnes par d√©faut (grands √©crans)
  - 2 colonnes si < 1024px
  - 1 colonne si < 768px
*/
.home-cards-container {
  width: 100%;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1rem;
  font-family: "Inter", sans-serif;
}

/* Carte individuelle */
.home-card {
  /* la classe border + bg-light sont d√©finies directement sur .icon-circle,
     donc ici on ne force pas de couleur */
  border: 1px solid var(--gray-200, #e5e7eb);
  border-radius: 0.5rem;
  display: flex;        /* Aligner ic√¥ne, titre, boutons sur la m√™me ligne */
  align-items: center;
  gap: 1rem;
  padding: 0.5rem 1rem;
  background-color: #fff;
}

/* Titre */
.card-title {
  font-size: 0.7rem;
  font-weight: 500;
  margin: 0;
}

/*
  Cercle (pour l'ic√¥ne) :
  - On laisse la classe border + bg-light dans le HTML
  - Dimensions identiques aux .action-btn
*/
.icon-circle {
  width: 1.6rem;
  height: 1.6rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 0.5rem;

  /* Couleur du texte (ic√¥ne) */
  color: var(--gray-800, #1f2937);
  font-size: 0.8rem;
}

/* Boutons align√©s √† droite dans la carte */
.card-actions {
  margin-left: auto;
  display: flex;
  gap: 0.5rem;
}

/* Boutons (m√™me taille que le cercle) */
.action-btn {
  width: 1.6rem;
  height: 1.6rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid var(--gray-200, #e5e7eb);
  border-radius: 0.5rem;
  cursor: pointer;
  color: var(--gray-700, #374151);
  transition: background-color 0.2s;
  font-size: 1rem; /* taille du +, et la fl√®che SVG est en 16x16 */
  line-height: 1;
  background: #fff;
}

.action-btn:hover {
  background-color: var(--gray-100, #f3f4f6);
}

/* 
  TABLETTE :
  - < 1024px => 2 colonnes
*/
@media (max-width: 1024px) {
  .home-cards-container {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* 
  MOBILE :
  - < 768px => 1 colonne => cartes empil√©es
*/
@media (max-width: 768px) {
  .home-cards-container {
    grid-template-columns: 1fr;
  }
}

