script:
/**
 * Exemple complet utilisant root_element pour le Shadow DOM.
 * Si votre Custom HTML Block est rendu via un moteur de template,
 * vous ne pouvez pas directement faire document.getElementById(...)
 * 
 * On appelle frappe.client.get_count pour chaque Doctype
 * et on met √† jour le texte dans le <span> correspondant.
 */

frappe.call({
  method: "frappe.client.get_count",
  args: { doctype: "Rendez Vous Client" },
  callback: function (r) {
    let el = root_element.querySelector("#count-rdv");
    if (el && r.message !== undefined) {
      el.innerText = r.message;
    }
  },
});

frappe.call({
  method: "frappe.client.get_count",
  args: { doctype: "Visite Commerciale" },
  callback: function (r) {
    let el = root_element.querySelector("#count-visite");
    if (el && r.message !== undefined) {
      el.innerText = r.message;
    }
  },
});

frappe.call({
  method: "frappe.client.get_count",
  args: { doctype: "Customer" },
  callback: function (r) {
    let el = root_element.querySelector("#count-customer");
    if (el && r.message !== undefined) {
      el.innerText = r.message;
    }
  },
});

frappe.call({
  method: "frappe.client.get_count",
  args: { doctype: "Item" },
  callback: function (r) {
    let el = root_element.querySelector("#count-item");
    if (el && r.message !== undefined) {
      el.innerText = r.message;
    }
  },
});

