// R√©cup√©rer l'√©l√©ment avec la classe "some-class" dans le framework Frappe
var someClassElement = root_element.querySelector('.some-class');
var nom = root_element.querySelector('.nom');

// Mettre √† jour le contenu de l'√©l√©ment avec la date format√©e

nom.innerHTML = "Bonjour <strong>" + title + "</strong>";
someClassElement.innerHTML = dateFormatee;

frappe.call({
    method: "frappe.client.get",
    args: {
        doctype: "User",
        name: frappe.session.user, // R√©cup√©rer l'utilisateur connect√©
    },
    callback(r) {
        if (r.message) {
            const userImageContainer = root_element.querySelector('.user-image-container');
            const userImageElement = root_element.querySelector('#imageUser');

            // Supprimer les enfants existants (au cas o√π)
            userImageContainer.innerHTML = "";

            // R√©cup√©rer l'image de l'utilisateur
            var userImageURL = r.message.user_image;

            // V√©rifier si l'image existe
            if (userImageURL) {
                // Afficher l'image utilisateur
                userImageElement.src = userImageURL;
                userImageElement.style.display = "block";
                userImageContainer.appendChild(userImageElement);
            } else {
                // G√©n√©rer un avatar avec les initiales
                const fullName = r.message.full_name || frappe.session.user_fullname || "Utilisateur";
                const initials = fullName.split(" ").map(word => word[0]).join("").slice(0, 2);

                // Cr√©er l'√©l√©ment avatar
                const avatarElement = document.createElement("div");
                avatarElement.className = "user-avatar";
                avatarElement.innerText = initials;

                userImageContainer.appendChild(avatarElement);
            }
        }
    }
});

style:
/* Conteneur principal */
.header-container {
  display: flex;
  flex-wrap: wrap; /* Permet aux √©l√©ments de passer √† la ligne si n√©cessaire */
  align-items: center; /* Aligne les √©l√©ments verticalement au centre */
  background-color: #ffffff; /* Fond blanc */
  padding: 1rem; /* Espacement interne */
  border-radius: 0.75rem; /* Coins arrondis */
  border: 0.5px solid #e5e7eb; /* Bordure grise tr√®s fine */
  width: 100%;
  gap: 1rem; /* Espacement entre les sections */
  box-sizing: border-box; /* Inclut le padding dans la largeur totale */
}

/* Conteneur de l'image utilisateur */
.user-image-container {
  flex: 0 0 auto; /* Ne s'√©tire pas */
  width: 3rem; /* Taille ajust√©e */
  height: 3rem;
  border-radius: 50%;
  overflow: hidden;
  border: 0.5px solid #e5e7eb;
  display: flex;
  justify-content: center;
  align-items: center;
}

/* Image utilisateur */
.user-image {
  width: 100%;
