style:
/* Conteneur principal */
.header-container {
  display: flex;
  justify-content: flex-start; /* Alignement à gauche */
  align-items: center;
  background-color: #ffffff; /* Fond blanc */
  padding: 1rem 1.5rem; /* Espacement interne */
  border-radius: 0.75rem; /* Coins arrondis */
  border: 0.5px solid #e5e7eb; /* Bordure grise très fine */
  width: 100%;
  margin: auto; /* Centrer horizontalement */
  gap: 0.5rem; /* Réduction de l'espacement entre les sections */
}



/* Contenu textuel */
.text-content {
  display: flex;
  flex-direction: column;
  justify-content: center; /* Centrer verticalement le texte */
  gap: 0.1rem; /* Espacement très réduit entre le titre et le sous-titre */
}

.title {
  font-size: 1.2rem; /* Taille réduite pour le titre */
  font-weight: 600; /* Semi-bold pour une bonne lisibilité */
  color: #1f2937; /* Couleur sombre */
  margin: 0; /* Supprimer les marges */
}

.subtitle {
  font-size: 0.7rem; /* Taille plus petite pour le sous-titre */
  font-weight: 400; /* Poids normal */
  color: #6b7280; /* Gris clair */
  margin: 0;
}

/* Contenu de la date */
.date-content {
  display: flex;
  align-items: center;
  gap: 0.25rem; /* Espacement réduit entre date et icône */
  margin-left: auto; /* Place la date à l'extrémité droite */
}

.date {
  font-size: 0.7rem; /* Taille de la date réduite */
  font-weight: 400; /* Poids normal */
  color: #1f2937; /* Couleur sombre */
  margin: 0;
  white-space: nowrap; /* Empêcher le retour à la ligne */
}

/* Icône */
.icon {
  width: 1.5rem; /* Taille réduite pour l'icône */
  height: 1.5rem;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #f3f4f6; /* Fond clair */
  border-radius: 50%; /* Forme ronde */
  color: #1f2937; /* Couleur sombre */
}

